#! /bin/bash
echo -e "\n $(date)" && echo -e "\n My Node on Shard 3 Status:\n" && ./hmy blockchain latest-headers | grep epoch && ./hmy blockchain latest-headers | grep viewID && ./hmy blockchain latest-headers | grep shardID && echo -e "\n Harmony Network Shard 3 Status:\n" && ./hmy --node="https://api.s3.t.hmny.io" blockchain latest-headers | grep epoch && ./hmy --node="https://api.s3.t.hmny.io" blockchain latest-headers | grep viewID && ./hmy --node="https://api.s3.t.hmny.io" blockchain latest-headers | grep shardID && echo -e "\n Uptime :: $(uptime)\n\n Harmony DB Size  ::  $(du -h harmony_db_0)\n Harmony DB Size  ::  $(du -h harmony_db_3)\n" && ./harmony -V